mapper.select:
select B.* from (select rownum as rnum, A.* from( select * from TB_BoardNotice where BNdelyn = 'N'
and BNtitle like '%'||#{keyword}||'%'
order by BNidx DESC) A where rownum <= #{perPageNum}*#{page}) B where B.rnum > #{pageStart}

mapper.update:
update TB_BoardNotice
set BNtitle = #{BNtitle}
, BNcontent = #{BNcontent}
where BNidx = #{BNidx}

update TB_BoardNotice
set BNhit = BNhit + 1
where BNidx = #{BNidx}

mapper.select:

select *
from(
	select /** INDEX_DESC(TB_BoardNotice) */
	rownum rn, BNidx, Midx, BNtitle, BNcontent, BNimage, BNwdate, BNmdate, BNdeln, BNhit
	from TB_BoardNotice
	where rownum <= #{pageNum} * #{amount}
)
where rn > (${pageNum} - 1) * #{amount}

mapper.update:
update TB_BoardNotice
set BNdelyn = 'Y'
where BNidx = #{BNidx}
